from secret import secretimport requestsimport refrom bs4 import BeautifulSoup, SoupStrainerimport coloramafrom colorama import Foreimport osclass Moodle:	def __init__(self, login_url, username, password, details=True, debug=True):		# place holder for saving pdfs to correct folder		self.folder = ""		self.details = details		self.debug = debug		self.session_requests = requests.session()		payload = {			"username": username,			"password": password		}		# log in to site		self.login_result = self.session_requests.post(			login_url,			data=payload,			headers=dict(referer=login_url)		)		if self.login_result.ok:			print(Fore.GREEN, "login successful", self.login_result.status_code)		else:			print(Fore.RED, "login unsuccessful", self.login_result.status_code)	@staticmethod	def parse_link(url):		# links always end with = and some number		# parse from href= until =[some_number(s)], then remove href		return re.findall(r'href=.+=[0-9]+', url)[0].replace('href="', "")	@staticmethod	def parse_title(url):		# get alphabetical, numerical and some puntuation/spaces between > and < tags		# (?<=>) start matching after > character, (?=<) end matching before < character		try:			# try  text between instance name and span, if that fails get last piece of text outside of tags			title = re.findall(r'(?<=instancename">).+(?=<span)', url)[-1]		except IndexError:			title = re.findall(r'(?<=>)[a-zA-Z0-9\s._\-]+(?=<)', url)[-1]		return title	def download_pdf(self, title, url):		if not title.endswith(".pdf"):			title += ".pdf"		# it's escapes all the way down		if "\\" in title:			title.replace("\\", "")		if "/" in title:			title.replace("/", "")		r = self.session_requests.get(url)		with open(self.folder + title, "wb") as pdffile:			pdffile.write(r.content)	def find_pdfs(self, url, visited=()):		'''		:param url: address to scrape pdfs from		:param visited: list of folders visited		:param details: print what's being visited and downloaded		:param debug: print error messages if true		:return:		'''		# add current link to visited		visited += (url,)		result = self.session_requests.get(url, headers=dict(referer=url))		for link in BeautifulSoup(result.content, "lxml", parse_only=SoupStrainer('a')):			current = str(link)			if "pdf" in current:				try:					pdf_url = self.parse_link(current)					title = self.parse_title(current)					if self.details:						print(Fore.GREEN, "pdf downloading", title, pdf_url)					self.download_pdf(title, pdf_url)				# if regex fails to parse pdf link				except IndexError:					if self.debug:						print(Fore.RED, "failed: pdf", current)			elif "folder" in current:				try:					new_link = self.parse_link(current)				# if regex fails, skip iteration in loop				except IndexError:					if self.debug:						print(Fore.RED, "failed: folder", current)					continue				# if parsed folder link is not the url the method was called on				if new_link not in visited:					if self.details:						print(Fore.YELLOW, "visiting folder", new_link)					return self.find_pdfs(new_link, visited)	def get_comp_modules(self):		# # get all the links to other COMP courses you're enrolled in from the home page		for link in BeautifulSoup(self.login_result.content, "lxml", parse_only=SoupStrainer('a')):			current = str(link)			if 'title="COMP' in current:				# allow for spaces between comp and module code, start match after title=" is found				module_code = re.findall(r'(?<=title=")COMP\s*[0-9]+', current)[0]				href = self.parse_link(current)				if not os.path.exists(module_code):					os.mkdir(module_code)					self.folder = module_code + "/"				if self.details:					print(Fore.BLUE, module_code, href)				self.find_pdfs(href)colorama.init()m = Moodle("https://csmoodle.ucd.ie/moodle/login/index.php", secret.username, secret.password)m.get_comp_modules()